void setup() {
  size(192, 157); 
  background(0); 
  frameRate(7);

  noStroke();
  colorMode(HSB); //HSB mode to have beautiful random colors
}

void draw () {
  x = Math.round(random(192)/16);
  y = Math.round(random(160)/16);
  fill(random(255), 200, 200);
  rect(x*16, y*16, 16, 16);
}

#!/bin/bash

start() {
    mkdir -p /tmp/mozilla
    rsync -avi --delete /home/user/.mozilla_save/ /home/user/.mozilla/
}

stop() {
    size=`du -xs /home/user/.mozilla/ | awk '{print $1}'`
    digits=`expr length $size`
    if [ $digits -gt 4 ]; then
        rsync -avi --delete /home/user/.mozilla/ /home/user/.mozilla_save/
    else
        echo 'no!'
        exit 0
    fi
}

case "$1" in
start)
     start
     ;;
stop)
     stop
     ;;
*)
     echo $"Usage: $0 {start|stop}"
     exit 1;;
esac
